import React, { useState } from "react";
import styles from "./NuevoVideo.module.css";

function NuevoVideo() {
  const [formValues, setFormValues] = useState({
    titulo: "",
    categoria: "",
    imagen: "",
    video: "",
    descripcion: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí agregar la lógica para enviar los datos del formulario
    console.log("Form submitted:", formValues);
  };

  const handleReset = () => {
    setFormValues({
      titulo: "",
      categoria: "",
      imagen: "",
      video: "",
    });
  };

  return (
    <div className={styles.formContainer}>
      <h1>Nuevo Video</h1>
      <p>Complete el formulario para crear una nueva tarjeta de video</p>
      <form onSubmit={handleSubmit} onReset={handleReset}>
        <div className={styles.formGroup}>
          <label>Título</label>
          <input
            type="text"
            name="titulo"
            value={formValues.titulo}
            onChange={handleInputChange}
            placeholder="Ingrese el título"
          />
        </div>
        <div className={styles.formGroup}>
          <label>Categoría</label>
          <select
            name="categoria"
            value={formValues.categoria}
            onChange={handleInputChange}
          >
            <option value="">Seleccione una categoría</option>
            <option value="60">60's</option>
            <option value="70">70's</option>
            <option value="80">80's</option>
            <option value="90">90's</option>
            <option value="2000">2000's</option>
          </select>
        </div>
        <div className={styles.formGroup}>
          <label>Imagen</label>
          <input
            type="text"
            name="imagen"
            value={formValues.imagen}
            onChange={handleInputChange}
            placeholder="El enlace es obligatorio"
            className={formValues.imagen ? "" : styles.error}
          />
        </div>
        <div className={styles.formGroup}>
          <label>Video</label>
          <input
            type="text"
            name="video"
            value={formValues.video}
            onChange={handleInputChange}
            placeholder="Ingrese el enlace del video"
          />
        </div>
        <div className={styles.formGroup}>
          <label>Descripción</label>
          <textarea
            name="descripcion"
            value={formValues.descripcion}
            onChange={handleInputChange}
            placeholder="¿De qué se trata este video?"
          />
        </div>
        <div className={styles.formActions}>
          <button type="submit" className={styles.guardar}>
            Guardar
          </button>
          <button type="reset" className={styles.limpiar}>
            Limpiar
          </button>
        </div>
      </form>
    </div>
  );
}

export default NuevoVideo;
