import React from "react";
import Banner from "components/Banner";
import Titulo from "components/Titulo";
import Card from "components/Card";
import styles from "./index.module.css";
import videos from "data/db.json";
import Slider from "react-slick";

function Inicio() {
  const categories = {
    60: "60's",
    70: "70's",
    80: "80's",
    90: "90's",
    2000: "2000's",
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 3,
    nextArrow: <SampleNextArrow />,
    prevArrow: <SamplePrevArrow />,
  };

  return (
    <div>
      <Banner img="BannerMain" color="black" />
      <Titulo>
        <h1>Â¡Los mejores rolones de todos los tiempos!</h1>
      </Titulo>
      {Object.entries(categories).map(([key, category]) => (
        <section key={key} className={styles.container}>
          <h2>{category}</h2>
          <Slider {...settings} className={styles.cardContainer}>
            {videos
              .filter((video) => video.category == key)
              .map((video) => (
                <Card key={video.id} {...video} />
              ))}
          </Slider>
        </section>
      ))}
    </div>
  );
}

function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "black" }}
      onClick={onClick}
    />
  );
}

function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "black" }}
      onClick={onClick}
    />
  );
}

export default Inicio;
